INSERT INTO "patient_exercise_assignments" VALUES (4,17,16,'7/13/2024','Step forward with one foot and bend both knees to about 90 degrees, lowering your body. Push off your front heel to bring the back leg forward into the next lunge. Keep your upper body tall and core engaged.',4);
