INSERT INTO "patient_exercise_assignments" VALUES (6,11,10,'6/20/2024','Lie on your stomach with arms extended. Simultaneously lift your arms, chest, and legs off the ground, squeezing your lower back and glutes. Hold for a moment, then gently lower back to the floor.',9);
INSERT INTO "patient_exercise_assignments" VALUES (7,8,7,'5/1/2024','Begin in a push-up position but rest on your forearms with elbows under your shoulders. Keep your body in a straight line from head to heels. Engage your abs and glutes, holding this position without letting your hips drop.',3);
INSERT INTO "patient_exercise_assignments" VALUES (8,2,1,'3/10/2025','Start in a high plank with hands shoulder-width apart. Keep your core tight as you lower your body until your chest is just above the floor, then push back up. Focus on steady movement and controlled breathing.',1);
INSERT INTO "patient_exercise_assignments" VALUES (9,20,19,'8/22/2024','Lie on your back with knees bent and feet flat. Press through your heels to lift your hips until your body forms a straight line from shoulders to knees. Squeeze your glutes at the top before lowering back down.',5);
INSERT INTO "patient_exercise_assignments" VALUES (10,23,22,'3/21/2024','Assume a high plank position, wrists under shoulders. Lift one hand to tap the opposite shoulder, then return it to the floor. Alternate sides, keeping hips as still as possible to engage your core.',6);
